mapper.update:
UPDATE employees
SET empPass = SHA2(#{empId}, 256)
WHERE empId = #{empId}

update employees
set empPass = SHA2(#{empPass}, 256),
    empPhoto = #{empPhoto}, empPhone = #{empPhone}, empTel = #{empTel}, 
    empEmail = #{empEmail}, empStatus = #{empStatus}, departmentName = #{departmentName},
    empPosition = #{empPosition}, empRole = #{empRole} 


    , empLeavedate = #{empLeavedate}


    , empRetiredate= #{empRetiredate}


    , empHiredate= #{empHiredate}

where empId = #{empId}
