mapper.update:
update employees
set empPhoto = #{empPhoto}, empPhone = #{empPhone}, empTel = #{empTel}, 
empEmail = #{empEmail}, empStatus = #{empStatus}, departmentName = #{departmentName},
empPosition = #{empPosition}, empHiredate = #{empHiredate}, empLeavedate = #{empLeavedate},
empRetiredate = #{empRetiredate}, empRole = #{empRole}
where empId = #{empId}
