mapper.update:
UPDATE notice 
SET noticeReadcount = noticeReadcount +1
WHERE noticeNum = #{noticeNum}

UPDATE notice
SET noticeSubject = #{noticeSubject}, noticeContent = #{noticeContent}, noticeCategory = #{noticeCategory}
WHERE noticeNum = #{noticeNum}

mapper.delete:
UPDATE notice
SET noticeStatus = '삭제'
WHERE noticeNum = #{noticeNum}
