mapper.select:
SELECT po.poCode, po.materialCode, po.materialName, po.poCount, po.poDate, po.payDate, po.materialPrice, po.poPrice, e.empName AS poOwner, po.cusName, po.poStatus FROM purchaseOrder po JOIN employees e ON po.poOwner = e.empid WHERE po.poStatus != '삭제됨'
and po.materialCode = #{search}
ORDER BY po.poCode DESC LIMIT #{startRow}, #{pageSize}

mapper.update:
UPDATE purchaseOrder
SET poStatus = '삭제됨'
WHERE poCode = #{poCode}

UPDATE purchaseOrder
SET poStatus = #{poStatus}
WHERE poCode = #{poCode}
