mapper.update:
		UPDATE purchaseOrder
		SET poCount = #{poCount}, poPrice = #{poPrice}, 
        	poOwner = #{poOwner},
        	cusName = #{cusName}, payDate = #{payDate} , materialCode = #{materialCode}, materialName = #{materialName}, materialPrice = #{materialPrice}
		WHERE poCode = #{poCode}

UPDATE purchaseOrder
SET poStatus = '삭제됨'
WHERE poCode = #{poCode}
