mapper.update:
UPDATE goodsReceipt
SET grCount = #{grCount},
    grOwner = (SELECT empid FROM employees WHERE empName = #{grOwner}),
    grDate = #{grDate},
    grStatus = #{grStatus}
WHERE grCode = #{grCode}

mapper.select:
	SELECT grCode, grCount, grEcount, grEdate, grStatus
    FROM goodsReceipt
    WHERE grCode = #{grCode}

mapper.update:
UPDATE goodsReceipt
SET grStatus = '삭제됨'
WHERE grCode = #{grCode}

UPDATE purchaseOrder
SET poStatus = '삭제됨'
WHERE poCode = #{poCode}
