mapper.select:
select a.roCode, a.cusCode, b.cusName, a.productCode, c.productName, c.productCount, a.roCount, a.roPrice, a.roDate, a.shipSdate, a.shipDate, a.roStatus, a.shipCount, a.shipPrice, a.roEmpId, d.EmpName as roEmpName from receivedOrder a left join customer b on a.cusCode = b.cusCode left join product c on a.productCode = c.productCode left join employees d on a.roEmpId = d.empId where a.roStatus != '삭제' and a.shipDate is null
and a.roCode like CONCAT('%',#{search},'%')
order by a.roCode desc limit #{startRow}, #{pageSize}

mapper.update:
UPDATE receivedOrder
SET roStatus = '삭제'
WHERE roCode = #{roCode}

UPDATE product
SET productCount = productCount - #{shipCount}
WHERE productCode = #{productCode}

		UPDATE receivedOrder
		SET roStatus = '삭제'
		WHERE roCode IN (
       
           #{roCode}
       
		)
