mapper.select:
select r.productCode,p.productName,r.materialCode,m.materialName, r.reqCount, m.materialUnit
from requirement r left join product p
on r.productCode = p.productCode
left join material m
on r.materialCode = m.materialCode

	where r.productCode = #{search} 


	or r.materialCode = #{search2}

order by r.productCode desc
limit #{startRow},#{pageSize}

select count(productCode) from requirement

	where productCode = #{search} 


	or materialCode = #{search2}

mapper.delete:
delete from requirement
where productCode = #{productCode}
and materialCode = #{materialCode}

mapper.update:
UPDATE requirement
SET reqCount=#{reqCount}
WHERE productCode = #{productCode}
and materialCode = #{materialCode}

mapper.delete:
DELETE from requirement

WHERE productCode = SUBSTRING('#{requireCode}', 1, 5)
and materialCode = SUBSTRING('#{requireCode}', 7);
